mapper.select:
select *,(select mc_balance from mz_medical_card where mc_Id_Card=tj.man_sid) mc_balance from tj_code_man tj
tj.man_id not in( SELECT tj_man_result.man_id FROM tj_man_result where tj_man_result.man_result is not null or tj_man_result.man_result != '' or tj_man_result.man_result is null group by tj_man_result.man_id ) /*名称like*/ and tj.man_name like '%${sermen}%'
/*身份证*/ or tj.man_sid like '%${sermen}%'
/*手机号*/ or tj.man_phone like '%${sermen}%'

select *,(select mc_balance from mz_medical_card where mc_Id_Card=tj.man_sid) mc_balance from tj_code_man tj
tj.man_id in( SELECT tj_man_result.man_id FROM tj_man_result where tj_man_result.man_result is null or tj_man_result.man_result ='' group by tj_man_result.man_id ) /*名称like*/ and tj.man_name like '%${sermen}%'
/*身份证*/ or tj.man_sid like '%${sermen}%'
/*手机号*/ or tj.man_phone like '%${sermen}%'
order by TO_DAYS(tj.man_time) = TO_DAYS(NOW()) desc

select *,(select mc_balance from mz_medical_card where mc_Id_Card=tj.man_sid) mc_balance from tj_code_man tj
tj.man_id in( SELECT tj_man_result.man_id FROM tj_man_result where tj_man_result.man_result is not null or tj_man_result.man_result != '' group by tj_man_result.man_id ) /*名称like*/ and tj.man_name like '%${sermen}%'
/*身份证*/ or tj.man_sid like '%${sermen}%'
/*手机号*/ or tj.man_phone like '%${sermen}%'
order by TO_DAYS(tj.man_time) = TO_DAYS(NOW()) desc
