mapper.select:
select * from staff yg inner join scheduling pb on pb.s_id=yg.s_id inner join frequency f on f.F_id=pb.fr_id inner join department_ks d on yg.ks_id=d.ks_id inner join title t on yg.t_id=t.t_id
to_days(pb.rq) = to_days(now()) and
yg.ks_Id = #{ksId}

select * from staff where s_id in(
select s_id from seltresultpatient where man_id = #{manId}
)

select * from seltpatientdoctorenjoin


        and s_id = #{sId}


        and ks_id = #{ksId}


        and (pt_no like '%${text}%' or pt_name like '%{text}%')

    and pt_is = 1

SELECT zy_patient_base.Pt_no, zy_patient_base.pt_In_date, zy_patient_base.Pt_name, zy_patient_base.pt_sex, zy_patient_base.Pt_Birth_date, zy_patient_base.Pt_Capacity_no, zy_patient_base.Pt_Home_adder, zy_patient_base.ks_id, zy_patient_base.Pt_Diagnose_name, zy_patient_base.Bd_id, zy_patient_base.Pt_Out_date, zy_patient_base.Pt_age, zy_patient_base.pt_pay_money, zy_patient_base.pt_price, zy_patient_base.pt_iphone, zy_patient_base.s_id, zy_patient_base.pt_sick_number, staff.s_id, staff.s_name, staff.s_sore, staff.s_phone, staff.s_date, staff.u_id, staff.ks_id, department_ks.ks_id, department_ks.ks_name, staff.t_id, zy_bed.Bd_id, zy_bed.Bd_name, zy_bed.Wd_id, zy_bed.bd_price, zy_bed.Bd_is, zy_bed.Pt_no FROM zy_patient_base LEFT JOIN staff ON zy_patient_base.s_id = staff.s_id LEFT JOIN department_ks ON zy_patient_base.ks_id = department_ks.ks_id inner JOIN zy_bed ON zy_patient_base.Bd_id = zy_bed.Bd_id
and zy_patient_base.s_id = #{sId}
and zy_patient_base.ks_id = #{ksId}
and (zy_patient_base.pt_no like '%${text}%' or zy_patient_base.pt_name like '%{text}%')
and zy_patient_base.pt_is = 1

select * from seltPatientAndPay



            and ( pt_pay_money between #{startPrice} and #{endPrice} or pt_pay_money between #{endPrice} and
            #{startPrice} )



            and pt_pay_money = #{startPrice}



            and pt_pay_money = #{endDate}





            and ( pt_price between #{startPrice} and #{endPrice} or pt_pay_money between #{endPrice} and
            #{startPrice} )


            and pt_price = #{startPrice}



            and pt_price = #{endDate}




        and pt_no like '%${searchLike}%' or pt_name like '%${searchLike}%'
