mapper.select:
SELECT zy_patient_base.Pt_no, zy_patient_base.pt_In_date, zy_patient_base.Pt_name, zy_patient_base.pt_sex, zy_patient_base.Pt_Birth_date, zy_patient_base.Pt_Capacity_no, zy_patient_base.Pt_Home_adder, zy_patient_base.ks_id, zy_patient_base.Pt_Diagnose_name, zy_patient_base.Bd_id, zy_patient_base.Pt_Out_date, zy_patient_base.Pt_age, zy_patient_base.pt_pay_money, zy_patient_base.pt_price, zy_patient_base.pt_iphone, zy_patient_base.s_id, staff.s_id, staff.s_name, staff.s_sore, staff.s_phone, staff.s_date, staff.u_id, staff.ks_id, department_ks.ks_id, department_ks.ks_name, staff.t_id, zy_bed.Bd_id, zy_bed.Bd_name, zy_bed.Wd_id, zy_bed.bd_price, zy_bed.Bd_is, zy_bed.Pt_no FROM zy_patient_base LEFT JOIN staff ON zy_patient_base.s_id = staff.s_id LEFT JOIN department_ks ON zy_patient_base.ks_id = department_ks.ks_id LEFT JOIN zy_bed ON zy_patient_base.Bd_id = zy_bed.Bd_id
and zy_patient_base.s_id = #{sId}
and zy_patient_base.ks_id = #{ksId}
and (zy_patient_base.pt_no like '%${text}%' or zy_patient_base.pt_name like '%{text}%')
and zy_patient_base.pt_is = 1
